sp_widget.script:
(function() {
  /* populate the 'data' object */
  /* e.g., data.table = $sp.getValue('table'); */
	var g1 = new GlideAggregate('x_800383_ajanta_x_ajanta_x_ray_details');
g1.addEncodedQuery('x_ray_type=Plane');
g1.addAggregate('COUNT');
g1.query();
while(g1.next()){
data.planeXray = g1.getAggregate('COUNT');
}
var g2 = new GlideAggregate('x_800383_ajanta_x_ajanta_x_ray_details');
g2.addEncodedQuery('x_ray_type=Digital^sys_created_onONToday@javascript:gs.beginningOfToday()@javascript:gs.endOfToday()');
g2.addAggregate('COUNT');
g2.query();
while(g2.next()){
data.digitalXRay = g2.getAggregate('COUNT');

}
	var g3 = new GlideAggregate('x_800383_ajanta_x_ajanta_x_ray_details');
g3.addEncodedQuery('x_ray_type=Dental^sys_created_onONToday@javascript:gs.beginningOfToday()@javascript:gs.endOfToday()');
g3.addAggregate('COUNT');
g3.query();
while(g3.next()){
data.dentalXRay = g3.getAggregate('COUNT');

}
	var g4 = new GlideAggregate('x_800383_ajanta_x_ajanta_x_ray_details');
g4.addEncodedQuery('sys_created_onONToday@javascript:gs.beginningOfToday()@javascript:gs.endOfToday()');
g4.addAggregate('COUNT');
g4.query();
while(g4.next()){
data.todayXRay = g2.getAggregate('COUNT');

}
		var g5 = new GlideAggregate('x_800383_ajanta_x_ajanta_x_ray_details');
g5.addEncodedQuery('x_ray_dateONThis month@javascript:gs.beginningOfThisMonth()@javascript:gs.endOfThisMonth()');
g5.addAggregate('COUNT');
g5.query();
while(g5.next()){
data.monthXRays = g5.getAggregate('COUNT');

}


})();
